    sqInt atomic;
    sqInt byteOffset;
    sqInt eightbyteOffset;
    sqInt fieldSize;
    sqInt isInt;
    sqInt recurse;
    sqInt registerType;
    unsigned int spec;
    unsigned int subIndex;
    sqInt subLevel;

	registerType = initialRegisterType;
	byteOffset = initialByteOffset;
	eightbyteOffset = initialEightbyteOffset;
